package Pocetak_OOP;

public class Return {
	public static void test () {
		int a = 10;
		System.out.println("BBBB");
		if (a == 10)
		return;
		System.out.println("CCCC");
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		System.out.println("AAAA");
		test();
		System.out.println("DDDD");
	}

}
